import React, { useState, useEffect } from 'react';
import axios from 'axios'; // Make sure to install axios: npm install axios

function ImageUpload() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [images, setImages] = useState([]);

  useEffect(() => {
    fetchImages();
  }, []); // Fetch images on component mount

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    if (selectedFile) {
      const formData = new FormData();
      formData.append('file', selectedFile);

      try {
        const response = await axios.post('http://localhost:9000/upload', formData, {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
        });
        console.log('Upload successful:', response.data);
        fetchImages(); // Refresh the images after upload
        // Handle success, display a success message or update state as needed
      } catch (error) {
        console.error('Error uploading file:', error);
        // Handle error, display an error message or update state as needed
      }
    }
  };

  const fetchImages = async () => {
    try {
      const response = await axios.get('http://localhost:9000/images');
      console.log('Images:', response.data.images);
      setImages(response.data.images || []);
    } catch (error) {
      console.error('Error fetching images:', error);
    }
  };

  const deleteImage = async (filename) => {
    try {
      const response = await axios.delete(`http://localhost:9000/image/${filename}`);
      console.log('Delete response:', response.data);
      fetchImages(); // Refresh images after deletion
      // Handle the delete response
    } catch (error) {
      console.error('Error deleting image:', error);
    }
  };

  return (
    <div>
      <input type="file" onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload</button>

      <div>
        <h2>Uploaded Images:</h2>
        <ul>
          {images.map((image, index) => (
            <li key={index}>
 <img src={`http://localhost:9000/img/${image}`} alt={`Uploaded Image ${index}`} style={{ width: '100px', height: 'auto' }} />

        <button onClick={() => deleteImage(image)}>Delete</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default ImageUpload;
